import { useEffect } from "react";
import axios from "axios";
import { useState } from "react";

const receiveMessages = async (idInstance, apiTokenInstance, setMessages) => {
  const url = `https://7103.api.greenapi.com/waInstance${idInstance}/ReceiveNotification/${apiTokenInstance}?receiveTimeout=20`;

  try {
    const response = await axios.get(url);
    console.log("Received response:", response.data); // Логируем весь ответ
    if (response.data) {
      const { receiptId, body } = response.data;

      await axios.delete(
        `https://7103.api.greenapi.com/waInstance${idInstance}/DeleteNotification/${apiTokenInstance}/${receiptId}`
      );

      // Проверка на входящее сообщение
      if (
        body.typeWebhook === "incomingMessageReceived" &&
        body.messageData.typeMessage === "textMessage"
      ) {
        const newMessage = {
          text: body.messageData.textMessageData.textMessage,
          isOutgoing: false,
        };
        setMessages((prevMessages) => [...prevMessages, newMessage]);
      }
    }
  } catch (error) {
    console.error(
      "Ошибка получения сообщения:",
      error.response ? error.response.data : error.message
    );
  }
};

const Chat = ({ idInstance, apiTokenInstance, phone }) => {
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    const checkMessages = async () => {
      await receiveMessages(idInstance, apiTokenInstance, setMessages);
      setTimeout(checkMessages, 5000); // Оптимизируйте таймаут, чтобы не было перегрузки
    };

    checkMessages();
  }, [idInstance, apiTokenInstance]);

  return (
    <div className="chat">
      <div className="messages">
        {messages.map((msg, index) => (
          <div key={index} className={msg.isOutgoing ? "outgoing" : "incoming"}>
            {msg.text}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Chat;
