import React, { useState, useEffect } from "react";
import axios from "axios";
import Message from "./Message";

const Chat = ({ idInstance, apiTokenInstance, phone }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");

  const sendMessage = async () => {
    if (!newMessage.trim()) return; // Проверка на пустое сообщение

    const url = `https://7103.api.greenapi.com/waInstance${idInstance}/SendMessage/${apiTokenInstance}`;
    const data = {
      chatId: `${phone}@c.us`,
      message: newMessage,
    };

    try {
      await axios.post(url, data);
      setMessages((prev) => [...prev, { text: newMessage, isOutgoing: true }]);
      setNewMessage("");
    } catch (error) {
      console.error("Ошибка отправки сообщения:", error);
    }
  };

  const receiveMessage = async () => {
    const url = `https://7103.api.greenapi.com/waInstance${idInstance}/ReceiveNotification/${apiTokenInstance}?receiveTimeout=20`;

    try {
      const response = await axios.get(url);
      if (!response.data) return;

      const { receiptId, body } = response.data;
      if (!receiptId || !body) return;

      await axios.delete(
        `https://7103.api.greenapi.com/waInstance${idInstance}/DeleteNotification/${apiTokenInstance}/${receiptId}`
      );

      if (
        body.typeWebhook === "incomingMessageReceived" &&
        body.messageData?.typeMessage === "textMessage"
      ) {
        const newMessage = {
          text: body.messageData.textMessageData.textMessage,
          isOutgoing: false,
        };
        setMessages((prevMessages) => [...prevMessages, newMessage]);
      }
    } catch (error) {
      if (error.response?.status === 404) {
        console.log("Новых уведомлений нет.");
      } else {
        console.error(
          "Ошибка получения сообщения:",
          error.response?.data || error.message
        );
      }
    }
  };

  useEffect(() => {
    const intervalId = setInterval(receiveMessage, 5000);
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="chat">
      <div className="messages">
        {messages.map((msg, index) => (
          <Message key={index} text={msg.text} isOutgoing={msg.isOutgoing} />
        ))}
      </div>
      <div className="input-area">
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Введите сообщение"
        />
        <button onClick={sendMessage}>Отправить</button>
      </div>
    </div>
  );
};

export default Chat;
